Real ${objective_name}LoopFunction::Sum(std::vector<Real>& elements) {
    Real temp = 0;
    for (Real value: elements)
        temp += value;
    return temp;
}

Real ${objective_name}LoopFunction::Dist(CVector2& pos1, CVector2& pos2) {
    return (pos1 - pos2).Length();
}

std::vector<Real> ${objective_name}LoopFunction::Dist(std::vector<CVector2>& positions, CVector2& pos1) {
    std::vector<CVector2> temp;
    for (CVector2 pos: positions)
        temp.push_back(Dist(pos, pos1));
    return temp;
}

std::vector<Real> ${objective_name}LoopFunction::Dist(CVector2& pos1, std::vector<CVector2> positions) {
    return Dist(positions, pos1);
}

std::vector<Real> ${objective_name}LoopFunction::Dist(std::vector<CVector2> positions1, std::vector<CVector2> positions2) {
    std::vector<CVector2> temp;
    for (int i = 0; i < std::min(positions1.size(), positions2.size()); i++)
        temp.push_back(Dist(positions1[i], positions2[i]));

    return temp;
}

std::vector<Real> ${objective_name}LoopFunction::DistX(std::vector<CVector2> positions1, std::vector<CVector2> positions2) {
    std::vector<CVector2> temp;
    for (int i = 0; i < positions1.size(); i++) {
        for (int j = 0; j < positions2.size(); j++) {
            temp.push_back(Dist(positions1[i], positions2[j]));
    }
    return temp;
}

