std::vector<CVector2> ClosestNeighbors(std::vector<CVector2>& positions) {
    std::vector<CVector2> temp;
    for (const CVector2& currentPos : positions) {
        Real minDistance = -1;
        CVector2 closestNeighbor;
        for (const CVector2& otherPos : positions) {
            if (&currentPos == &otherPos) continue;
            Real distance = (currentPos - otherPos).Length();
            if (distance < minDistance || minDistance < 0) {
                minDistance = distance;
                closestNeighbor = otherPos;
            }
        }
        temp.push_back(closestNeighbor);
    }
    return temp;
}
